DROP TABLE IF EXISTS admin;
CREATE TABLE admin (
   id int(11) NOT NULL auto_increment,
   username varchar(20),
   password varchar(255),
   level int(11),
   PRIMARY KEY (id)
);




DROP TABLE IF EXISTS danhmuc;
CREATE TABLE danhmuc (
   id int(11) NOT NULL auto_increment,
   id_parent int(11),
   ten varchar(255),
   tukhoa varchar(255),
   kieunhap int(11),
   capnhat datetime,
   PRIMARY KEY (id)
);




DROP TABLE IF EXISTS giohang;
